#!/bin/bash

find . -type f -name "log.txt" | xargs grep "clang" | sed 's/^.*;\s//g'| sed 's/-o\s.*\.o\s//g' | sed 's/$/ --analyze -Xanalyzer -analyzer-output=html -Xanalyzer -analyzer-disable-checker -Xanalyzer core.CallAndMessage,core.DivideZero,core.NonNullParamChecker,core.NullDereference,core.NullDereference,core.UndefinedBinaryOperatorResult,core.VLASize,core.uninitialized.ArraySubscript,core.uninitialized.Assign,core.uninitialized.Branch,core.uninitialized.CapturedBlockVariable,core.uninitialized.UndefReturn,cplusplus.InnerPointer,cplusplus.Move,cplusplus.NewDelete,cplusplus.NewDeleteLeaks,cplusplus.PlacementNew,cplusplus.PureVirtualCall,deadcode.DeadStores,nullability.NullPassedToNonnull,nullability.NullReturnedFromNonnull,security.insecureAPI.UncheckedReturn,security.insecureAPI.getpw,security.insecureAPI.gets,security.insecureAPI.mkstemp,security.insecureAPI.mktemp,security.insecureAPI.vfork,unix.API,unix.MallocSizeof,unix.MismatchedDeallocator,unix.Vfork,unix.cstring.BadSizeArg,unix.cstring.NullArg -Xanalyzer -analyzer-config -Xanalyzer ipa=none -o \/RSFF\/Bugdetector\/result/' >> staticAnalyse-cmd.txt
